"""Getting data for multiple stocks using yfinance library@author: Jeffrey Li"""import datetime as dtimport pandas as pdimport yfinance as yfstocks = ["2800.HK", "0005.HK", "2888.HK", "0883.HK"]start = dt.datetime.today() - dt.timedelta(30)end = dt.datetime.today()close_price = pd.DataFrame()ohlcv_data = {} for ticker in stocks:        close_price[ticker] = yf.download(ticker, start, end, interval = "5m")["Adj Close"]        close_price["HSBC_SCB_delta"] = close_price["0005.HK"]/close_price["2888.HK"]close_price["HSBC_SCB_delta"].plot()close_price.plot(subplots=True, layout=(5,1), title="HSI Stocks")